generated.css:
h1,
h2,
h3,
h4,
h5 {
  margin: 0;
  padding: 0;
}

html,
body {
  margin: 0;
  padding: 0;
}

body {
  background-color: #f4f4f5;
  overflow: hidden;
  display: grid;
  grid-template-rows: 1fr 3fr 0.5fr;
  height: 100vh;
}

.header {
  margin-top: 50px;
  padding: 0px 50px;
  display: flex;
  align-items: center;
}

.header__menu {
  font-weight: bold;
  font-size: 36px;
  margin-right: 20px;
}

.header__title {
  font-size: 36px;
  font-weight: bold;
}

.portfolio {
  padding: 40px;
  display: grid;
  padding-left: 90px;
  grid-template-columns: minmax(250px, 1fr);
  grid-auto-columns: minmax(250px, 1fr);
  grid-gap: 50px;
  grid-auto-flow: column;
  overflow: scroll;
  grid-template-rows: 45vh;
  grid-auto-rows: 45vh;
  padding-bottom: 50px;
  align-self: center;
}

.portfolio::-webkit-scrollbar {
  display: none;
}

.portfolio .portfolio__list {
  background-image: url("https://cdn.pixabay.com/photo/2017/03/22/17/39/kitchen-2165756__340.jpg");
  background-position: center center;
  background-size: cover;
  box-shadow: 0 19px 38px rgba(0, 0, 0, 0.3), 0 15px 38px rgba(0, 0, 0, 0.22);
  display: flex;
  align-items: flex-end;
  justify-content: center;
}

.portfolio__list .portfolio__info {
  text-align: center;
  background-color: white;
  width: 100%;
  padding: 30px 0;
}

.navigation {
  width: 100%;
  background-color: white;
  display: flex;
  justify-content: space-between;
  align-items: center;
  color: rgba(0, 0, 0, 0.5);
  padding: 0px 40px;
}

.navigation svg {
  fill: rgba(0, 0, 0, 0.5);
}

.navigation .navigation__list {
  list-style: none;
  padding: 0;
  margin: 0;
  display: grid;
  grid-auto-columns: repeat(auto-fill, minmax(min-content, 1fr));
  grid-template-columns: repeat(auto-fill, minmax(min-content, 1fr));
  grid-auto-flow: column;
  grid-gap: 20px;
}

.navigation__icons .navigation__icon:last-child {
  margin-left: 10px;
}

.navigation .navigation__item--selected {
  color: #2c2c2c;
}
